<html lang="en">
<head>
  <meta charset="utf-8" />
  <meta name="viewport" content="width=device-width,initial-scale=1" />
  <title>Printable Postcard Template</title>
  <style>
    :root{
      --card-w:6in;      /* postcard width for printing */
      --card-h:4in;      /* postcard height for printing */
      --bleed:0.125in;   /* optional bleed */
      --safe:0.25in;     /* safe margin */
      --bg:#f8f5f2;
      --accent:#b34dff;
      --muted:#6b7280;
      font-family: Inter, system-ui, -apple-system, 'Segoe UI', Roboto, 'Helvetica Neue', Arial;
    }

    /* Page layout (on screen we'll scale down) */
    body{display:flex;gap:24px;align-items:flex-start;justify-content:center;padding:40px;background:linear-gradient(180deg,#eef2ff,white);color:#111}
    .toolbar{position:fixed;left:16px;top:16px;display:flex;gap:8px}
    button{background:#111;color:white;border:0;padding:8px 12px;border-radius:8px;cursor:pointer}
    .wrap{display:grid;grid-template-columns:1fr 1fr;gap:18px;align-items:start}

    /* Postcard preview container scales to fit screen but retains real print size */
    .postcard{
      width:var(--card-w);
      height:var(--card-h);
      box-shadow:0 10px 30px rgba(0,0,0,0.12);
      border-radius:12px;
      overflow:hidden;
      transform-origin:top left;
      background:var(--bg);
      border:1px solid rgba(0,0,0,0.06);
    }

    /* scale down preview on small screens */
    @media (max-width:1200px){
      .postcard{transform:scale(0.75)}
    }
    @media (max-width:900px){
      .wrap{grid-template-columns:1fr}
      .postcard{transform:scale(0.6)}
    }

    /* The two sides: front (image & message) and back (address & stamp) */
    .sides{display:flex;height:100%}
    .side{flex:1;display:flex;flex-direction:column;justify-content:space-between;padding:calc(var(--safe));}

    /* Front styling */
    .front{background:linear-gradient(135deg,#ffd7ea 0%, #bcd3ff 100%);position:relative}
    .photo{height:62%;border-radius:10px;background-size:cover;background-position:center;box-shadow:inset 0 0 30px rgba(0,0,0,0.06)}
    .title{font-size:20px;margin-top:8px;font-weight:700}
    .message{flex:1;margin-top:8px;font-size:14px;color:var(--muted);line-height:1.4}
    .editable{outline:none;min-height:2.2em}

    /* Back styling */
    .back{background:linear-gradient(180deg,#fff,#fbfbff);font-size:13px;padding:calc(var(--safe));}
    .back .top{display:flex;justify-content:space-between;align-items:flex-start}
    .stamp{width:1.4in;height:1.4in;border-radius:6px;border:2px dashed rgba(0,0,0,0.12);display:flex;align-items:center;justify-content:center;font-weight:700;color:var(--muted);}
