<body>
  <div class="toolbar">
    <button onclick="window.print()">Print postcard</button>
    <button onclick="downloadPNG()">Download PNG</button>
  </div>

  <div class="wrap">
    <!-- Live postcard preview -->
    <div class="postcard" id="postcard">
      <div class="sides">
        <div class="side front" id="front">
          <div class="photo" id="photo" contenteditable="true" title="Click to edit or paste an image (use URL background via controls)">Add a photo or click the controls to change background</div>
          <div>
            <div class="title editable" id="title" contenteditable="true">Wish you were here!</div>
            <div class="message editable" id="message" contenteditable="true">Dear friend,
              <div style="height:6px"></div>
              Having a lovely time — the sunsets are unbelievable and the food is amazing. Can't wait to tell you all about it.
            </div>
          </div>
        </div>

        <div class="side back" id="back">
          <div class="top">
            <div class="stamp">STAMP</div>
            <div class="address">
              <div class="line editable" contenteditable="true">Recipient name</div>
              <div class="line editable" contenteditable="true">Street address</div>
              <div class="line editable" contenteditable="true">City, State ZIP</div>
              <div class="line editable" contenteditable="true">Country</div>
            </div>
          </div>
          <div style="display:flex;justify-content:space-between;align-items:center;margin-top:10px">
            <div class="sender editable" contenteditable="true">From: Your name</div>
            <div class="note">Postcard size: 6 × 4 in — print single-sided or two pages for front + back</div>
          </div>
        </div>
      </div>
    </div>

    <!-- Controls -->
    <div class="controls">
      <div>
        <label for="bgurl">Front background image URL</label>
        <input id="bgurl" class="input" placeholder="paste an image URL and press Apply" />
        <div style="display:flex;gap:8px;margin-top:8px">
          <button onclick="applyBg()">Apply</button>
          <button onclick="resetBg()">Reset</button>
        </div>
      </div>

      <div>
        <label>Quick text</label>
        <button onclick="setQuick('Greetings from here!')">Short greeting</button>
        <button onclick="setQuick('Having a wonderful trip — see you soon!')">Travel note</button>
        <button onclick="setQuick('Thinking of you — lots of love!')">Affection</button>
      </div>

      <div>
        <label>Tips</label>
        <div class="note">Click any text on the postcard to edit. Use Print (recommended) and choose "Actual size" or "Scale: 100%" in your browser print dialog for correct physical dimensions.</div>
      </div>
    </div>
  </div>

  <script>
    const photo = document.getElementById('photo');
    const bgurl = document.getElementById('bgurl');

    function applyBg(){
      const url = bgurl.value.trim();
      if(!url) return alert('Paste an image URL first');
      photo.style.backgroundImage = `url('${url}')`;
      photo.innerText = '';
    }
    function resetBg(){
      photo.style.backgroundImage = '';
      photo.innerHTML = 'Add a photo or click the controls to change background';
      bgurl.value = '';
    }

    function setQuick(text){
      document.getElementById('message').innerText = text;
    }

    /* Download PNG: simple approach using SVG foreignObject -> canvas
       Works best in modern browsers. If it fails, use Print -> Save as PDF or Print to physical card.
    */
    async function downloadPNG(){
      const node = document.getElementById('postcard');
      const w = Math.round(6*300); // 6 inches at 300 DPI
      const h = Math.round(4*300);

      // Serialize node to SVG via foreignObject
      const xml = new XMLSerializer();
      const cloned = node.cloneNode(true);
      // Inline computed background images as style if present
      // (simple: convert url('...') to style background-image)
      const front = cloned.querySelector('#front');

      const svg = `<?xml version="1.0" encoding="UTF-8"?>\n`+
        `<svg xmlns='http://www.w3.org/2000/svg' width='${w}' height='${h}'>`+
        `<foreignObject width='100%' height='100%'>`+
        `<div xmlns='http://www.w3.org/1999/xhtml' style='width:100%;height:100%;'>`+
        `${node.outerHTML}`+
        `</div>`+
        `</foreignObject></svg>`;

      const img = new Image();
      const svgBlob = new Blob([svg],{type:'image/svg+xml;charset=utf-8'});
      const url = URL.createObjectURL(svgBlob);
      img.onload = function(){
        const c = document.createElement('canvas');
        c.width = w; c.height = h;
        const ctx = c.getContext('2d');
        // white background
        ctx.fillStyle = '#ffffff'; ctx.fillRect(0,0,w,h);
        ctx.drawImage(img,0,0,w,h);
        URL.revokeObjectURL(url);
        const png = c.toDataURL('image/png');
        const a = document.createElement('a');
        a.href = png; a.download = 'postcard.png';
        a.click();
      };
      img.onerror = function(e){
        alert('Download failed in this browser. Use Print -> Save as PDF or a different browser.');
      }
      img.src = url;
    }
  </script>
</body>
</html>
